import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const MerchantConfig = () => {
  const navigate = useNavigate();
  const [config, setConfig] = useState({
    merchantId: '',
    username: '',
    password: '',
    gatewayUrl: 'https://test-gateway.mastercard.com',
    apiVersion: '82',
    currency: 'USD',
    merchantName: 'Test Merchant',
    merchantUrl: 'https://example.com'
  });

  const [showPassword, setShowPassword] = useState(false);
  const [savedConfig, setSavedConfig] = useState(null);

  useEffect(() => {
    // Load saved configuration from localStorage
    const saved = localStorage.getItem('merchantConfig');
    if (saved) {
      const parsedConfig = JSON.parse(saved);
      setConfig(parsedConfig);
      setSavedConfig(parsedConfig);
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setConfig(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSave = () => {
    // Validate required fields
    if (!config.merchantId || !config.username || !config.password) {
      alert('Please fill in all required fields: Merchant ID, Username, and Password');
      return;
    }

    // Save to localStorage
    localStorage.setItem('merchantConfig', JSON.stringify(config));
    setSavedConfig(config);
    alert('Configuration saved successfully!');
  };

  const handleTest = async () => {
    if (!savedConfig) {
      alert('Please save configuration first');
      return;
    }

    try {
      const response = await fetch('http://localhost:3001/health');
      const data = await response.json();
      alert(`Backend connection successful!\nStatus: ${data.status}\nTime: ${data.timestamp}`);
    } catch (error) {
      alert(`Backend connection failed!\nError: ${error.message}\n\nMake sure the backend is running on port 3001`);
    }
  };

  const handleContinue = () => {
    if (!savedConfig) {
      alert('Please save configuration before continuing');
      return;
    }
    navigate('/checkout');
  };

  const handleLoadDefaults = () => {
    setConfig({
      merchantId: 'TESTMPGSTEST0101',
      username: 'merchant.TESTMPGSTEST0101',
      password: '12acbd9acc25c2cc1dca44a1c2be2a9c',
      gatewayUrl: 'https://test-gateway.mastercard.com',
      apiVersion: '82',
      currency: 'USD',
      merchantName: 'Test Merchant',
      merchantUrl: 'https://example.com'
    });
  };

  const handleClear = () => {
    if (window.confirm('Are you sure you want to clear the configuration?')) {
      localStorage.removeItem('merchantConfig');
      setConfig({
        merchantId: '',
        username: '',
        password: '',
        gatewayUrl: 'https://test-gateway.mastercard.com',
        apiVersion: '82',
        currency: 'USD',
        merchantName: 'Test Merchant',
        merchantUrl: 'https://example.com'
      });
      setSavedConfig(null);
      alert('Configuration cleared');
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Merchant Configuration</h1>
        <p style={styles.subtitle}>Configure your Mastercard Gateway credentials</p>
      </div>

      {savedConfig && (
        <div style={styles.successBanner}>
          ‚úì Configuration saved for merchant: {savedConfig.merchantId}
        </div>
      )}

      <div style={styles.formContainer}>
        <div style={styles.section}>
          <h2 style={styles.sectionTitle}>Gateway Credentials</h2>

